import React, { useEffect, useState, Component } from 'react';
import Select from 'react-select';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';


class Dropdown extends Component {

  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      id: "",
      title: '',
      text: ''
    }
  }

 async getOptions(){
    const res = await axios.get('/rating')
    const data = res.data

    const options = data.map(d => ({
      "value" : d.id,
      "label" : d.title,
      "content": d.text

    }))

    this.setState({selectOptions: options})

  }

  handleChange(e){
   this.setState({id:e.value, title:e.label, text:e.content})
  }

  componentDidMount(){
      this.getOptions()
  }

  render() {
    console.log(this.state.selectOptions)
    return (
      <div>
        <Select placeholder="Please choose text" options={this.state.selectOptions} onChange={this.handleChange.bind(this)} />
    <p>You have selected <strong>{this.state.title}</strong></p>
    <br />
    <br />
    <p><strong>{this.state.text}</strong></p>
      </div>
    )
  }
}

export default Dropdown;
