import React, {Component} from "react";
import {Redirect, Link} from "react-router-dom";


export const read = (textId, token) => {
    return fetch(`${process.env.REACT_APP_API_URL}/rating/${textId}`, {
        method: "GET",
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`
        }
    })
        .then(response => {

            return response.json();
        })
        .catch(err => console.log(err));
};


class RateTextbyId extends Component {
    constructor() {
      super();
      this.state = {
        id: "",
        error: ""
      };
    }

    init = textId => {
        read(textId).then(data => {
        this.setState({ text: data });
        })
      };


    componentDidMount() {
      const textId = this.props.match.params.textId;
      this.init(textId);
    }


    render() {

    }
}

  export default RateTextbyId;
