import React, { useEffect, useState, Component } from 'react';
import Select from 'react-select';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Textform from './textlevelform';
import ParagraphLevel from './paragraphlevelform';



class Dropdown extends Component {

  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      id: "",
      title: '',
      text: '',
      category: '',
      text1: '',
      text2: '',
      text3: '',
      text4: '',
      text5: ''
    }
  }


 async getOptions(){
    const res = await axios.get('/rating')
    const data = res.data

    const options = data.map(d => ({
      "value" : d._id,
      "label" : d.title,
      "content": d.text
    }))

    this.setState({selectOptions: options})

  }

  handleChange(e){
   this.setState({id:e.value, title:e.label, text:e.content})
   }

   handleChangeText(e){
    this.setState({text1:e.text1, text2:e.text2, text3:e.text3, text4:e.text4, text5:e.text5})
    }


  handleCategoryChange = (e) => {
    this.setState({category:e.target.value})
}

  componentDidMount(){
      this.getOptions()
    }

  handleClick(event) {
      event.preventDefault();
      const newtextLevel = {
          text1: this.state.text1,
          text2: this.state.text2,
          text3: this.state.text3,
          text4: this.state.text4,
          text5: this.state.text5
      }

      console.log(`${newtextLevel}`)
      axios.post('http://localhost:8080/create', newtextLevel)

      this.setState({
      text1: '',
      text2: '',
      text3: '',
      text4: '',
      text5: ''
        });
  }

  render() {
    console.log(this.state.selectOptions)
    if (this.state.id) 
     return (
      <div className="container">
                <div className="row">
            <div className="col">
            <div className="box" style={{backgroundColor:"white"}}>
    < Select placeholder="Please choose text" options={this.state.selectOptions} onChange={this.handleChange.bind(this)} />
    <h3 style={{marginTop:'5%'}}><strong>{this.state.title}</strong></h3>
     <p style={{marginTop:'5%', whiteSpace:"pre-line", fontSize:'80%'}}>{this.state.text}</p>
            </div>
            </div>
            <div className="col">
            <div className="box" style={{backgroundColor:"white"}}>
        <select name="category" onChange={this.handleCategoryChange.bind(this)}>
            <option id="0" >Text level</option>
            <option id="1" >Paragraph level</option>
        </select>
        <p>{this.state.category === "Text level" && (
         <div className='container' style={{marginTop:'5%', fontSize:"80%"}}>

         <form>

         <p style={{marginTop:'3%', fontWeight:'bold'}}>How many main ideas does this text discuss?</p>
             <div className="form-group">
                 <input onChange={handleChange} name="text1" value={text1} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
             </div>

         <p style={{fontWeight:'bold'}}>How many of the main ideas of the text are forecast in the introduction? (If there is no introduction, write N/A)</p>
             <div className="form-group">
                 <input onChange={handleChange} name="text2" value={text2} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
             </div>

         <p style={{fontWeight:'bold'}}>How many of the main ideas of the text are summarized in the conclusion? (If there is no conclusion, write N/A.)</p>
             <div className="form-group">
                 <input onChange={handleChange} name="text3" value={text3} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
             </div>

         <p style={{fontWeight:'bold'}}>On a scale of 1 to 4, how well does the essay address the topic of the prompt?</p>
             <div className="form-group">
                 <input onChange={handleChange} name="text4" value={text4} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
             </div>

         <p style={{fontWeight:'bold'}}>On a scale of 1 to 4, rate the strength of the thesis statement. (Use N/A if there is no thesis statement.)</p>
             <div className="form-group">
                 <input onChange={handleChange} name="text5" value={text5} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%',width:'20%'}}></input>
             </div>

             <button style={{marginLeft:'45%'}} onClick={handleClick} className="btn btn-lg btn-info">DONE</button>
         </form>
         </div>)}</p>
        <p>{this.state.category === "Paragraph level" && (
        <ParagraphLevel />)}</p>
    </div>
    </div>
     </div>
     </div>)
    else {
    return (
      <div>
      < Select placeholder="Please choose text" options={this.state.selectOptions} onChange={this.handleChange.bind(this)} />
      </div>
    )}  
}
};

export default Dropdown;
