import React, {Component} from "react";
import {isAuthenticated} from "../auth";
import {Redirect, Link} from "react-router-dom";
import {read} from "./apiUser";

class Profile extends Component {
    constructor() {
      super();
      this.state = {
        user: "",
        redirectToSignin: false,
        error: ""
      };
    }

    init = userId => {
        const token = isAuthenticated().token;
        read(userId, token).then(data => {
          if (data.error) {
            this.setState({ redirectToSignin: true });
          } else {
            this.setState({ user: data });
          }
        })
      };


    componentDidMount() {
      const userId = this.props.match.params.userId;
      this.init(userId);
    }


    render() {
      const { redirectToSignin, user } = this.state;
      if (redirectToSignin) return <Redirect to="/signin" />;
      if (user.account === "Teacher") {
      return (
        <div className="container">
          <div className="row">
          <div className="col-md-6">
          <h2 className="mt-5 mb-5">Profile</h2>
          <div className="row">
            <div className="col-md-4">
            </div>
            <div className="col-md-8">
              <div className="lead mt-2">
                <p>Hello {user.name} :)</p>
                <p>Account type: {user.account}</p>
                <p>{`You joined on ${new Date(user.created).toDateString()}`}</p>

                <p style={{ marginTop:"20%"}}>For help or suggestions: nsollier@iastate.edu</p>
              </div>
            </div>
          </div>
          </div>
          <div className="col-md-6">
            {isAuthenticated().user && isAuthenticated().user._id == user._id && (
              <div className="d-inline-block mt-5">
                <Link className="btn btn-raised mr-5" to={`/user/edit/${user._id}`}>
                  Edit profile
                </Link>
                <button className="btn btn-raised mr-5">Delete account</button>
                </div>
                )}
          </div>
          </div>
        </div>
      )}
      else {
        return (
          <div className="container">
          <div className="row">
          <div className="col-md-6">
          <h2 className="mt-5 mb-5">Profile</h2>
          <div className="row">
            <div className="col-md-4">
            </div>
            <div className="col-md-8">
              <div className="lead mt-2">
                <p>Hello {user.name} :)</p>
                <p>Account type: {user.account}</p>
                <p>{`You joined on ${new Date(user.created).toDateString()}`}</p>

                <p style={{ marginTop:"20%"}}>For help or suggestions: nsollier@iastate.edu</p>
              </div>
            </div>
          </div>
          </div>
          <div className="col-md-6">
            {isAuthenticated().user && isAuthenticated().user._id == user._id && (
              <div className="d-inline-block mt-5">
                <Link className="btn btn-raised mr-5" to={`/user/edit/${user._id}`}>
                  Edit profile
                </Link>
                <button className="btn btn-raised mr-5">Delete account</button>
                </div>
                )}
          </div>
          </div>
        </div>
        )
      };
    }
}

  export default Profile;
