import React, { useState, Component } from 'react';
import Dropdown from './dropdown';
import axios from "axios";

const Title = props => {

    const [input, setInput] = useState({
                title: '',
                text1: '',
                text2: '',
                text3: '',
                text4: '',
                text5: ''
            })

            function handleChange(event) {
                const {name, value} = event.target;

                setInput(prevInput => {
                    return {
                        ...prevInput,
                        [name]: value
                    }
                })
            };

            function handleClick(event) {
                event.preventDefault();
                const newtextLevel = {
                    title: input.title,
                    text1: input.text1,
                    text2: input.text2,
                    text3: input.text3,
                    text4: input.text4,
                    text5: input.text5
                }
                console.log(newtextLevel);

                axios.post('http://localhost:8080/create', newtextLevel)

                setInput({
                title: '',
                text1: '',
                text2: '',
                text3: '',
                text4: '',
                text5: ''
                  });
            }

    return (
      <div>
        <h3>{props.title}</h3>
        <p> Number of paragraphs: {props.paragraphnum}</p>
      </div>
    )
  }

export default Title;
