import React, { useEffect, useState, Component } from 'react';
import { useParams, Redirect } from "react-router-dom";
import Select from 'react-select';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';



class Dropdown extends Component {

  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      id: "",
      title: '',
      text: '',
      readyToRedirect: false,
    }
  }

 async getOptions(){
    const res = await axios.get('/rating')
    const data = res.data

    const options = data.map(d => ({
      "value" : d._id,
      "label" : d.title,
      "content": d.text

    }))

    this.setState({selectOptions: options})

  }

  handleChange(e){
   this.setState({id:e.value, title:e.label, text:e.content, readyToRedirect:true})

   }

  componentDidMount(){
      this.getOptions()
      fetch(
        `http://localhost:8080/api/channel/${this.state.id}`)
    }

  render() {
    console.log(this.state.selectOptions)
    return (
      <div>
        < Select placeholder="Please choose text" options={this.state.selectOptions} onChange={this.handleChange.bind(this)} />
       <h3 style={{marginTop:'5%'}}><strong>{this.state.title}</strong></h3>
       <p style={{marginTop:'5%', whiteSpace:"pre-line"}}>{this.state.id}</p>
       <p style={{marginTop:'5%', whiteSpace:"pre-line"}}>{this.state.text}</p>
       </div>

    )   
}
};

export default Dropdown;
