import React, {useEffect, useState} from 'react';
import Dropdown from './dropdown';
import './rating.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function RateText() {

    const [notes, setNotes] = useState([{
        title: '',
        text: ''
    }])



    useEffect(() => {
        fetch("/rating").then(res => {
           if(res.ok) {
               return res.json()
           }
        }).then(jsonRes => setNotes(jsonRes));
    })

    return (
        <div className="container">
            <div className="row">
        <div className="col">
          <div className="box">
          <Dropdown style={{width:"100px"}} />

{notes.map(note =>
<div style={{marginTop:'5%'}}> 
    <h1>{note.title}</h1>
    <p>{note.text}</p>
</div>
)}</div>
        </div>
        <div className="col">
          <div className="box">
    <form>
          <div className="form-group">
                <p>How many main ideas does this text discuss? "Main idea" = distinct topics within the paper that speak directly to the thesis. Generally, you would expect to see one "main idea"
per body paragraph.</p>
            </div>
    </form>
</div>
</div>
    </div>

    </div>
    )};

export default RateText;
