import React, {useEffect, useState} from 'react';
import ReactDOM from "react-dom";
import axios from 'axios';

function Dropdown() {

    const [notes, setNotes] = useState([{
        title: '',
        text: ''
    }])

    useEffect(() => {
        fetch("/rating").then(res => {
           if(res.ok) {
               return res.json()
           }
        }).then(jsonRes => setNotes(jsonRes));
    })

    return (
        <div className="container">
            <h1>Rate text</h1>

            {notes.map(note =>
            <div> 
                <h1>{note.title}</h1>
                <p>{note.text}</p>
                <Dropdown>
                    <Dropdown.Toggle value={this.state.value} onChange={this.handleChange} variant="success" id="dropdown-basic">
                    Dropdown Button
                    </Dropdown.Toggle>

                <Dropdown.Menu>
                    <Dropdown.Item value="grapefruit">{note.title}</Dropdown.Item>
                    <Dropdown.Item href="#/action-2">{note.title}</Dropdown.Item>
                    <Dropdown.Item href="#/action-3">{note.title}</Dropdown.Item>
                </Dropdown.Menu>
                </Dropdown>
            <form>
            <label>
              Pick text to rate:
              <select value={this.state.value} onChange={this.handleChange}>
                <option value="grapefruit">Grapefruit</option>
                <option value="lime">Lime</option>
                <option value="coconut">Coconut</option>
                <option value="mango">Mango</option>
              </select>
            </label>
          </form>
          </div>
            )}
        </div>
    )};

export default Dropdown;
