import React, {useEffect, useState, Component} from 'react';
import Dropdown from './dropdown';
import axios from 'axios';
import Select from 'react-select';
import Textform from './textlevelform';
import ParagraphLevel from './paragraphlevelform';
import './rating.css';
import 'bootstrap/dist/css/bootstrap.min.css';


function RateText() {

    const [notes, setNotes] = useState([{
        title: '',
        text: ''
    }])

    const [category, setCategory] = React.useState('');

    const handleCategoryChange = (category) => {
       setCategory(category);
   }

    useEffect(() => {
        fetch("/rating").then(res => {
           if(res.ok) {
               return res.json()
           }
        }).then(jsonRes => setNotes(jsonRes));
    })


        return (
            <div className="container">
                <div className="row">
            <div className="col">
            <div className="box">
                <Dropdown />
            </div>
            </div>
        <div className="col">
          <div className="box">
          <select name="category" value={category} onChange={event => handleCategoryChange(event.target.value)}>
            <option id="0" >Text level</option>
            <option id="1" >Paragraph level</option>

        </select>
        <p>{category === "Text level" && (
        <Textform />)}</p>
        <p>{category === "Paragraph level" && (
        <ParagraphLevel />)}</p>

</div>
</div>
    </div>

    </div>

    )};

export default RateText;
