import React, { Component } from 'react';
import Select from 'react-select';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import Textform from './textlevelform';
import ParagraphLevel from './paragraphlevelform';


class RaterSelection extends Component {

  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      name: ''
    }
  }

 async getOptions(){
    const res = await axios.get('/rating')
    const data = res.data

    const options = data.map(d => ({
      "value" : d.name
    }))

    this.setState({selectOptions: options})
  }

  handleChange(e){
   this.setState({name:e.value})
   }


  handleCategoryChange = (e) => {
    this.setState({category:e.target.value})
}

  componentDidMount(){
      this.getOptions()
    }


  render() {
    console.log(this.state.selectOptions)

    if (this.state.name) 
     return (
      <div className="container">
                <div className="row">
            <div className="col">
            <div className="box" style={{backgroundColor:"white"}}>
    < Select placeholder="Please select your name" options={this.state.selectOptions} onChange={this.handleChange.bind(this)} />
      <h3 style={{marginTop:'5%'}}><strong>{this.state.name}</strong></h3>
            </div>
            </div>
     </div>
     </div>)
    else {
    return (
        <form style={{marginTop:'1%', marginLeft:'.5%'}} onSubmit={this.handleSubmit}>
        <label>
          Rater:
          <input type="text" value={this.state.value} onChange={this.handleChange.bind(this)} />
        </label>
        <input type="submit" value="Submit" />
      </form>
    )}  
}
};

export default RaterSelection;
