import React, { Component } from 'react';
import Dropdown from './dropdown.js';
import Textform from './textlevelform';


export default class Rater extends React.Component {
    constructor(props) {
      super(props);
      this.state = {rater: ''};
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange = event => {
      event.persist();

      let value = event.target.value;

      this.setState(prevState => ({
        item: { ...prevState.item,  [event.target.name]: value }
      }))
    };

    handleSubmit(event) {
      event.preventDefault();
      this.setState({ value: event.target.value });
    }

    render() {

      return (

        <div style={{marginTop:"1%"}}>
        <form onSubmit={this.handleSubmit}>
          <label style={{marginLeft:"1%"}}>
            <input 
            type="text"
            name="rater"
            placeholder="rater name"
            value={this.state.rater}
            onChange={this.handleChange}
            value={this.state.value} />
          </label>
          <p>Currently rating: { this.state.rater }</p>
          <input type="submit" value="Submit" />
        </form>

        <Dropdown rater={this.state.rater} />
        </div>
      );
    }
  }

