import React, {useEffect, useState} from 'react';
import Dropdown from './dropdown';
import Textform from './textlevelform';
import ParagraphLevel from './paragraphlevelform';
import './rating.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function RateText() {

    const [notes, setNotes] = useState([{
        title: '',
        text: ''
    }])

    const [category, setCategory] = React.useState('');

    const handleCategoryChange = (category) => {
       setCategory(category);
   }

    useEffect(() => {
        fetch("/rating").then(res => {
           if(res.ok) {
               return res.json()
           }
        }).then(jsonRes => setNotes(jsonRes));
    })

    if (this.category === "Text level") {

        return (
            <div className="container">
                <div className="row">
            <div className="col">
            <div className="box">
                <Dropdown />
            </div>
            </div>
        <div className="col">
          <div className="box">
          <select name="category" value={category} onChange={event => handleCategoryChange(event.target.value)}>
            <option id="0" >Text level</option>
            <option id="1" >Paragraph level</option>
            <Textform />
        </select>
</div>
</div>
<p>Currently selected: {category}</p>
    </div>

    </div>

    )}

    else {
        return (
            <div className="container">
                <div className="row">
            <div className="col">
            <div className="box">
                <Dropdown />
            </div>
            </div>
        <div className="col">
          <div className="box">
          <select name="category" value={category} onChange={event => handleCategoryChange(event.target.value)}>
            <option id="0" >Text level</option>
            <option id="1" >Paragraph level</option>
        </select>
</div>
</div>
<p>Currently selected: {category}</p>
    </div>

    </div>
        )
    }

};

export default RateText;
