import React, {useState} from 'react';
import axios from "axios";

const textlevel = [
    {
        title: 'Text 1',
        text1: 'john@email.com',
        text2: '111-111-111',
        text3: '',
        text4: '',
        text5: ''
      }
    ];

export class Textform extends React.Component {

      constructor() {
        super();
        this.state = {
          textquestions: textlevel
        };
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleSubmit(e) {
        e.preventDefault();
        const
        { textquestions } = this.state,
        title = this.refs.title.value,
        text1 = this.refs.text1.value,
        text2 = this.refs.text1.value,
        text3 = this.refs.text1.value,
        text4 = this.refs.text1.value,
        text5 = this.refs.text1.value;
        this.setState({
          textquestions: [...textquestions, {
            title,
            text1,
            text2,
            text3,
            text4,
            text5
          }]
        }, () => {
          this.refs.title.value = '';
          this.refs.text1.value = '';
          this.refs.text2.value = '';
          this.refs.text3.value = '';
          this.refs.text4.value = '';
          this.refs.text5.value = '';
        });
      }

      render() {
        const { textquestions } = this.state;
        console.log('message',this.state.textquestions);
        return (   
          <div>
            <h2>Add Someone</h2>
            <form onSubmit={this.handleSubmit}>
              <input type="text" ref="name" placeholder="name" />
              <input type="text" ref="email" placeholder="email" />
              <input type="text" ref="phone" placeholder="phone" />
              <button type="submit">Submit</button>
