import React, {useState} from 'react';
import axios from "axios";

function Textform() {

    const [input, setInput] = useState({
        title: '',
        q1: none,
        q2: '',
        q3: '',
        q4: '',
        q5: ''
    })

    function handleChange(event) {
        const {name, value} = event.target;

        setInput(prevInput => {
            return {
                ...prevInput,
                [name]: value
            }
        })
    };

    function handleClick(event) {
        event.preventDefault();
        const newForm = {
            title: input.title,
            q1: input.q1,
            q2: input.q2,
            q3: input.q3,
            q4: input.q4,
            q5: input.q5
        }

        axios.post('http://localhost:8080/create', newForm)

        setInput({
        title: '',
        q1: '',
        q2: '',
        q3: '',
        q4: '',
        q5: ''
          });
    }

    return ( 
        <div className='container' style={{marginTop:'5%'}}>
            <h1 style={{textAlign:'center'}}>
                Text level
            </h1>

        <form>
        <p style={{marginTop:'3%', fontWeight:'bold'}}>Please specify text (i.e: "Text 1")</p>
            <div className="form-group">
                <input onChange={handleChange} name="title" autoComplete="off" value={input.title} autoComplete="off" placeholder="Text 1" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
            </div>

        <p style={{marginTop:'3%', fontWeight:'bold'}}>How many main ideas does this text discuss?</p>
            <div className="form-group">
                <input onChange={handleChange} name="q1" autoComplete="off" value={input.q1} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
            </div>

        <p style={{fontWeight:'bold'}}>How many of the main ideas of the text are forecast in the introduction? (If there is no introduction, write N/A)</p>
            <div className="form-group">
                <input onChange={handleChange} name="q2" autoComplete="off" value={input.q2} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
            </div>

        <p style={{fontWeight:'bold'}}>How many of the main ideas of the text are summarized in the conclusion? (If there is no conclusion, write N/A.)</p>
            <div className="form-group">
                <input onChange={handleChange} name="q3" autoComplete="off" value={input.q3} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
            </div>

        <p style={{fontWeight:'bold'}}>On a scale of 1 to 4, how well does the essay address the topic of the prompt?</p>
            <div className="form-group">
                <input onChange={handleChange} name="q4" autoComplete="off" value={input.q4} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
            </div>

        <p style={{fontWeight:'bold'}}>On a scale of 1 to 4, rate the strength of the thesis statement. (Use N/A if there is no thesis statement.)</p>
            <div className="form-group">
                <input onChange={handleChange} name="q5" autoComplete="off" value={input.q5} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%',width:'20%'}}></input>
            </div>


            <button style={{marginLeft:'45%'}} onClick={handleClick} className="btn btn-lg btn-info">DONE</button>
        </form>
        </div>
    )};

export default Textform;
