import React, {useState} from 'react';
import axios from "axios";

function ParagraphLevel() {

    const questions = [
		{
			questionText: 'How many main ideas are discussed in this paragraph? See definition of a "main idea" above. To distinguish between "main ideas" and simply supporting ideas, consider how main ideas are broad; supporting ideas can be subsumed under a broader main idea category. Paragraphs may provide clues such as transitions to indicate separate ideas.',
			answerOptions: [
				{ answerText: '0' },
				{ answerText: '1' },
                { answerText: '2' },
                { answerText: '3' },
                { answerText: '4' },
                { answerText: '5' },
                { answerText: '6' },
                { answerText: '7' },
                { answerText: '8' },


			],
		},
		{
			questionText: 'How well does this paragraph develop from / fit with the thesis statement (if applicable) and prompt?',
			answerOptions: [
                { answerText: 'NA'},
				{ answerText: '1'},
				{ answerText: '2'},
				{ answerText: '3'},
				{ answerText: '4'},
			],
		},
		{
			questionText: 'How well does this paragraph connect to/transition from/follow the previous paragraph? Write "N/A" if there is only one paragraph in the whole text.',
			answerOptions: [
                { answerText: 'NA'},
				{ answerText: '1'},
				{ answerText: '2'},
				{ answerText: '3'},
				{ answerText: '4'},
			],
		},
		{
			questionText: 'How well does this paragraph evidence the claim made in the topic sentence? Write N/A if there is no topic sentence.',
			answerOptions: [
                { answerText: 'NA'},
				{ answerText: '1'},
				{ answerText: '2'},
				{ answerText: '3'},
				{ answerText: '4'},
			],
		},
        {
			questionText: 'On a scale of 1 to 4, how strong is this paragraph\'s topic sentence? Write N/A if there is no topic sentence.',
			answerOptions: [
                { answerText: 'NA'},
				{ answerText: '1'},
				{ answerText: '2'},
				{ answerText: '3'},
				{ answerText: '4'},
			],
		},
	];

    const [currentQuestion, setCurrentQuestion] = useState(0);

    const handleAnswerOptionClick = (event) => {
        const {name, value} = event.target;
        event.preventDefault();
        const answer = event.value;
        axios.post('http://localhost:8080/create', answer)

        const nextQuestion = currentQuestion + 1;
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		}
	};

    const [input, setInput] = useState({
        title: '',
        q1: '',
        q2: '',
        q3: '',
        q4: '',
        q5: ''
    })

    function handleChange(event) {
        const {name, value} = event.target;

        setInput(prevInput => {
            return {
                ...prevInput,
                [name]: value
            }
        })
    };

    function handleClick(event) {
        event.preventDefault();
        const newForm = {
            title: input.title,
            q1: input.q1,
            q2: input.q2,
            q3: input.q3,
            q4: input.q4,
            q5: input.q5
        }

        console.log(`${newForm}`)
        axios.post('http://localhost:8080/create', newForm)

        setInput({
        title: '',
        q1: '',
        q2: '',
        q3: '',
        q4: '',
        q5: ''
          });
    }

    return ( 
        <div className='container' style={{marginTop:'5%'}}>
            <h1 style={{textAlign:'center'}}>
                Paragraph level
            </h1>

        <form>
        <p style={{marginTop:'3%', fontWeight:'bold'}}>Please specify text (i.e: "Text 1")</p>
            <div className="form-group">
                <input onChange={handleChange} name="title" value={input.title} autoComplete="off" placeholder="Text 1" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
            </div>

        <p style={{marginTop:'3%', fontWeight:'bold'}}>How many main ideas are discussed in this paragraph? See definition of a "main idea" above. To distinguish between "main ideas" and simply supporting ideas, consider how
main ideas are broad; supporting ideas can be subsumed under a broader main idea category. Paragraphs may provide clues such as transitions to indicate separate ideas.</p>
            <div className="form-group">
                <input onChange={handleChange} name="q1" value={input.q1} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
            </div>

        <p style={{fontWeight:'bold'}}>How well does this paragraph develop from / fit with the thesis statement (if applicable) and prompt?</p>
            <div className="form-group">
                <input onChange={handleChange} name="q2" value={input.q2} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
            </div>

        <p style={{fontWeight:'bold'}}>How well does this paragraph connect to/transition from/follow the previous paragraph? Write "N/A" if there is only one paragraph in the whole text.</p>
            <div className="form-group">
                <input onChange={handleChange} name="q3" value={input.q3} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
            </div>

        <p style={{fontWeight:'bold'}}>How well does this paragraph evidence the claim made in the topic sentence? Write N/A if there is no topic sentence.</p>
            <div className="form-group">
                <input onChange={handleChange} name="q4" value={input.q4} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%', width:'20%'}}></input>
            </div>

        <p style={{fontWeight:'bold'}}>On a scale of 1 to 4, how strong is this paragraph's topic sentence? Write N/A if there is no topic sentence.</p>
            <div className="form-group">
                <input onChange={handleChange} name="q5" value={input.q5} autoComplete="off" placeholder="Enter number" className="form-control" style={{marginTop:'2%',width:'20%'}}></input>
            </div>

            <button style={{marginLeft:'45%'}} onClick={handleClick} className="btn btn-lg btn-info">DONE</button>
        </form>
        </div>
    )};

export default ParagraphLevel;
