import React, {Component} from "react";
import {Redirect, Link} from "react-router-dom";


export const read = (textId, token) => {
    return fetch(`${process.env.REACT_APP_API_URL}/rating/${textId}`, {
        method: "GET",
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`
        }
    })
        .then(response => {

            return response.json();
        })
        .catch(err => console.log(err));
};


class RateTextbyId extends Component {
    constructor() {
      super();
      this.state = {
        id: "",
        error: ""
      };
    }

    init = textId => {
        const token = isAuthenticated().token;
        read(textId, token).then(data => {
          if (data.error) {
            this.setState({ redirectToSignin: true });
          } else {
            this.setState({ text: data });
          }
        })
      };


    componentDidMount() {
      const textId = this.props.match.params.textId;
      this.init(textId);
    }


    render() {
      return (
        <div className="container">
          <div className="row">
          <div className="col-md-6">
          <h2 className="mt-5 mb-5">Profile</h2>
          <div className="row">
            <div className="col-md-4">
            </div>
            <div className="col-md-8">
              <div className="lead mt-2">
                <p>Hello {user.name} :)</p>
                <p>Account type: {user.account}</p>
                <p>{`You joined on ${new Date(user.created).toDateString()}`}</p>

                <p style={{ marginTop:"20%"}}>For help or suggestions: nsollier@iastate.edu</p>
              </div>
            </div>
          </div>
          </div>
          <div className="col-md-6">
            {isAuthenticated().user && isAuthenticated().user._id == user._id && (
              <div className="d-inline-block mt-5">
                <Link className="btn btn-raised mr-5" to={`/user/edit/${user._id}`}>
                  Edit profile
                </Link>
                <button className="btn btn-raised mr-5">Delete account</button>
                </div>
                )}
          </div>
          </div>
        </div>
      )
    }
}

  export default Profile;
