import React, {useState} from 'react';
import axios from "axios";

function UploadText() {

    const [input, setInput] = useState({
        title: '',
        text: ''
    })

    function handleChange(event) {
        const {name, value} = event.target;

        setInput(prevInput => {
            return {
                ...prevInput,
                [name]: value
            }
        })
    };

    function handleClick(event) {
        event.preventDefault();
        const newNote = {
            title: input.title,
            text: input.text
        }

        axios.post('http://localhost:8080/create', newNote)

        setInput({
            title: '',
            text: ''
          });
    }

    return ( 
        <div className='container' style={{marginTop:'5%'}}>
            <h1>
                Upload text
            </h1>
        <form>
            <div className="form-group">
                <input onChange={handleChange} name="title" value={input.title} autoComplete="off" placeholder="Enter file title" className="form-control" style={{width:'20%'}}></input>
            </div>

            <div className="form-group">
            <textarea onChange={handleChange} placeholder="Enter text" autoComplete="off" name="text" value={input.text} className="form-control" style={{width:'50%', height:'500px', resize:'none'}}>

            </textarea>
            </div>

            <button onClick={handleClick} className="btn btn-lg btn-info">UPLOAD</button>
        </form>
        </div>
    )};

export default UploadText;
