import React, {useEffect, useState} from 'react';
import Dropdown from './dropdown';
import Textform from './textlevelform';
import ParagraphLevel from './paragraphlevelform';
import './rating.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function RateText() {

    const [notes, setNotes] = useState([{
        title: '',
        text: ''
    }])



    useEffect(() => {
        fetch("/rating").then(res => {
           if(res.ok) {
               return res.json()
           }
        }).then(jsonRes => setNotes(jsonRes));
    })

    return (
        <div className="container">
            <div className="row">
        <div className="col">
          <div className="box">
          <Dropdown />
          </div>
        </div>
        <div className="col">
          <div className="box">
          <div>
        <Select placeholder="Please choose text" options={this.state.selectOptions} onChange={this.handleChange.bind(this)} />
    <h3 style={{marginTop:'5%'}}><strong>{this.state.title}</strong></h3>
    <p style={{marginTop:'5%', whiteSpace:"pre-line"}}>{this.state.text}</p>
      </div>
          <Textform />
          <ParagraphLevel />
</div>
</div>
    </div>

    </div>
    )};

export default RateText;
