import React, { useEffect, useState, Component } from 'react';
import Select from 'react-select';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const titleIneed = async values => {
  const res = await fetch({ url: 'http://localhost:8080/create', method: "POST", body: values });
  const data = await res.json();
  return data;
};

class Dropdown extends Component {

  constructor(props){
    super(props)
    this.state = {
      selectOptions : [],
      id: "",
      title: '',
      text: '',
      paragraphs: ''
    }
  }

 async getOptions(){
    const res = await axios.get('/rating')
    const data = res.data

    const options = data.map(d => ({
      "value" : d.id,
      "label" : d.title,
      "content": d.text

    }))

    this.setState({selectOptions: options})

  }

  handleChange(e){
   this.setState({id:e.value, title:e.label, text:e.content})
   }

  divideText = (e) => {
    this.setState({id:e.value, title:e.label, text:e.content})
    if (this.text) {
      const paragraphnum = this.text.split('\n\n');
      this.setState({ pragraphs: localStorage.getItem(paragraphnum.length) });
    }
  }


  componentDidMount(){
      this.getOptions()
  }

  render() {
    console.log(this.state.selectOptions)
    return (
      <div>
        < Select placeholder="Please choose text" options={this.state.selectOptions} onChange={this.handleChange.bind(this), this.divideText} />
        <p>{this.state.paragraphs}</p>
       <h3 style={{marginTop:'5%'}}><strong>{this.state.title}</strong></h3>
       <p style={{marginTop:'5%', whiteSpace:"pre-line"}}>{this.state.text}</p>
       </div>
    )
  }
}

export default Dropdown;
