import React, {useState} from 'react';
import NavigateBeforeIcon from '@material-ui/icons/NavigateBefore';
import NavigateNextIcon from '@material-ui/icons/NavigateNext';

function ParagraphCount(props) {
    const [count, setCount] = useState(1);
    const [input, setInput] = useState({
        paragraph: count,
        currentparagraph: ''
    })


    function onClickPrevious(){
        if (count > 1) {
            setCount(prevCount => prevCount - 1)
        }
    }

    function onClickNext(){
        if (count < props.paragraphnum) {
            setCount(prevCount => prevCount + 1)
        }
    }

    let entiretext = props.text;
    let splittedtext = entiretext.split(/\r?\n/);
    let filteredtext = splittedtext.filter(item => item);
    let currentparagraph = filteredtext[count-1];

    return ( 
        <div className='container' style={{marginTop:'5%', fontSize:"80%", backgroundColor:'white'}}>
       
        <p style={{padding:"1%", borderStyle:'solid', backgroundColor:'#252d4a', fontSize:'16px', fontWeight:'500', color:'white', borderColor:'darkgray'}}>{currentparagraph}</p>
        <h3 style={{backgroundColor:'white'}}><NavigateBeforeIcon onClick={onClickPrevious} style={{fontSize:'200%', cursor:'pointer', backgroundColor:'white'}}/> 
        paragraph {count}/{props.paragraphnum} 
        <NavigateNextIcon onClick={onClickNext} style={{fontSize:'200%', cursor:'pointer', backgroundColor:'white'}}/></h3>

        </div>
    )};

export default ParagraphCount;
